import React from "react";

const Birthday = ({ name, birthday, birthdays, setBirthdays, dob, age }) => {

    const deleteHandler = () => {
        setBirthdays(birthdays.filter(el => el.id!==birthday.id))
    }

    const remainingDays = () => {

        var today = new Date();
        var todayDate = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();
        const todayDateArray = todayDate.split('-');

        const dobArray = dob.split('-');
        
        console.log(todayDateArray);

        const oneDay = 24 * 60 * 60 * 1000; // hours*minutes*seconds*milliseconds
        const firstDate = new Date(dobArray[0], dobArray[1], dobArray[2]);
        const secondDate = new Date(todayDateArray[0], todayDateArray[1], todayDateArray[2]);
        const diffDays = Math.round(Math.abs((firstDate - secondDate) / oneDay));
        return diffDays;
    }

    return (    
        <div className="todo">
            <li className="todo-item"><b>{name}</b> ({age} years) {remainingDays()} days left</li>
            <button onClick={deleteHandler} className="trash-btn">
                <i className="fas fa-trash"></i>
            </button>
        </div>
    );
};

export default Birthday;
