import React from "react";


const Form = ({ name, setName, dob, setDob }) => {

    const nameHandler = (e) => {
        console.log(e.target.value)
        setName(e.target.value)
    }

    const dobHandler = (e) => {
        setDob(e.target.value)
    }

    return (
    <form>
      <input onChange={nameHandler} type="text" className="todo-input" />
      <input onChange={dobHandler} type="date" />

      <button onClickclassName="todo-button" type="submit">
        <i className="fas fa-plus-square"></i>
      </button>

    </form>
    );
};

export default Form;
