import React from "react";


const Form = ({ name, setName, dob, setDob, birthdays, setBirthdays, age, setAge }) => {

    const nameHandler = (e) => {
        setName(e.target.value)
    }

    const dobHandler = (e) => {
        setDob(e.target.value)
    }



    const submitButtonHandler = (e) => {
        e.preventDefault();
        
        var userinput = document.getElementById("DOB").value;
    var dob = new Date(userinput);
    if(userinput==null || userinput=='') {
      document.getElementById("message").innerHTML = "**Choose a date please!";  
      return false; 
    } else {
    
    //calculate month difference from current date in time
    var month_diff = Date.now() - dob.getTime();
    
    //convert the calculated difference in date format
    var age_dt = new Date(month_diff); 
    
    //extract year from date    
    var year = age_dt.getUTCFullYear();
    
    //now calculate the age of the user
    var age = Math.abs(year - 1970);
    
    //display the calculated age
    return document.getElementById("result").innerHTML =  
             "Age is: " + age + " years. ";
    }


        setBirthdays([...birthdays, { 'name': name, 'dob': dob, id: Math.random() * 1000 }]);
        setName('');

    }

    return (
    <form>
      <input onChange={nameHandler} value={name} type="text" className="todo-input" />
      <input onChange={dobHandler} type="date" />

      <button onClick={submitButtonHandler} className="todo-button" type="submit">
        <i className="fas fa-plus-square"></i>
      </button>

    </form>
    );
};

export default Form;
