import React from "react";

const Birthday = ({text, birthdays, setBirthdays, birthday}) => {

    const deleteHandler = () => {
        setBirthdays(birthdays.filter(el => el.id !== birthday.id))
    };

    const completeHandler = () => {
        setBirthdays(birthdays.map((item) => {
            if (item.id == birthday.id) {
                return {
                    ...item,
                    completed: !item.completed
                }
            }
            return item;
        }));
    };


    return (
        <div className="todo">
            <li className={`todo-item ${birthday.completed ? "completed" : "" }`}>{text}</li>
            <button onClick={completeHandler} className="complete-btn">
                <i className="fas fa-check"></i>
            </button>
            <button onClick={deleteHandler} className="trash-btn">
                <i className="fas fa-trash"></i>
            </button>
        </div>
    );
};

export default Birthday;
