import React , { useState, useEffect } from 'react';
import './App.css';

import Form from './components/Form';
import BirthdayList from './components/BirthdayList';

function App() {

  const [inputText, setInputText] = useState("");
  const [birthdays, setBirthdays] = useState([]);
  const [status,setStatus] = useState("all");
  const [filteredBirthdays, setFilteredBirthdays] = useState([]);

  //Run once when the app starts
  useEffect(() => {
    getLocalBirthdays();
  }, []);

  useEffect(() => {
    filterHandler();
    saveLocalBirthdays();
  }, [birthdays,status]);


  const filterHandler = () => {
    switch(status) {
      case 'completed':
        setFilteredBirthdays(birthdays.filter(birthday => birthday.completed === true));
        break;
      case 'uncompleted':
        setFilteredBirthdays(birthdays.filter(birthday => birthday.completed === false));
        break;
      default:
        setFilteredBirthdays(birthdays);
        break;
    }
  };

  //Save to Local
  const saveLocalBirthdays = () => {
      localStorage.setItem('birthdays',JSON.stringify(birthdays));
  };

  const getLocalBirthdays = () => {
    if(localStorage.getItem('birthdays') === null) {
      localStorage.setItem('birthdays', JSON.stringify([]));
    } else {
      let birthdayLocal = JSON.parse(localStorage.getItem('birthdays'));
      setBirthdays(birthdayLocal);
    }
  }


  return (
    <div className="App">
      <header>
        <h1>Birthday Remainder</h1>
      </header>
      <Form birthdays={birthdays} 
            setBirthdays={setBirthdays} 
            inputText={inputText} 
            setInputText={setInputText}
            setStatus={setStatus}           
            />
      <BirthdayList birthdays={birthdays} setBirthdays={setBirthdays} filteredBirthdays={filteredBirthdays} />
    </div>
  );
}

export default App;
