import React from "react";


const Form = ({ name, setName, dob, setDob, birthdays, setBirthdays }) => {

    const nameHandler = (e) => {
        console.log(e.target.value)
        setName(e.target.value)
    }

    const dobHandler = (e) => {
        setDob(e.target.value)
    }

    const submitButtonHandler = () => {
        setBirthdays([...birthdays, { name: name, dob: dob, id: Math.random() * 1000 }])
        console.log(birthdays)
    }

    return (
    <form>
      <input onChange={nameHandler} type="text" className="todo-input" />
      <input onChange={dobHandler} type="date" />

      <button onClick={submitButtonHandler} className="todo-button" type="submit">
        <i className="fas fa-plus-square"></i>
      </button>

    </form>
    );
};

export default Form;
