import React from "react";
import BirthdayList from "./BirthdayList";

const Birthday = ({ name,birthday, birthdays, setBirthdays, dob }) => {

    const deleteHandler = () => {
        setBirthdays(birthdays.filter(el => el.id!==birthday.id))
    }

    function fnCalculateAge(){

        var userDateinput = DOB  
       
        // convert user input value into date object
        var birthDate = new Date(userDateinput);
         console.log(" birthDate"+ birthDate);
        
        // get difference from current date;
        var difference=Date.now() - birthDate.getTime(); 
             
        var  ageDate = new Date(difference); 
        var calculatedAge=   Math.abs(ageDate.getUTCFullYear() - 1970);
        alert(calculatedAge);
   }


    return (
        <div className="todo">
            <li className="todo-item">{name}</li>
            <li className="todo-item">{dob}{Date.now}</li>
            <button onClick={deleteHandler} className="trash-btn">
                <i className="fas fa-trash"></i>
            </button>
        </div>
    );
};

export default Birthday;
