import React from "react";


const Form = ( {inputText, setInputText, birthdays, setBirthdays, setStatus} ) => {

  const inputTextHandler = (e) => {
    setInputText(e.target.value)
  };

  const submitBirthdayHandler = (e) => {
    e.preventDefault();
    setBirthdays([...birthdays,{ text: inputText, completed: false, id: Math.random() * 1000 }])
    setInputText('')
  };

  const statusHandler = (e) => {
    setStatus(e.target.value);
  }

    return (
    <form>
      <input value={inputText} onChange={inputTextHandler} type="text" className="todo-input" />
      <button onClick={submitBirthdayHandler} className="todo-button" type="submit">
        <i className="fas fa-plus-square"></i>
      </button>
    </form>
    );
};

export default Form;
