import React from "react";


const Form = ({ name, setName, dob, setDob, birthdays, setBirthdays, age, setAge }) => {

    const nameHandler = (e) => {
        setName(e.target.value)
    }

    const dobHandler = (e) => {
        setDob(e.target.value)
    }



    const submitButtonHandler = (e) => {
        e.preventDefault();
        var diff_ms = Date.now() - dob.getTime();
        var age_dt = new Date(diff_ms); 
        setAge(Math.abs(age_dt.getUTCFullYear() - 1970));
        setBirthdays([...birthdays, { 'name': name, 'dob': dob, id: Math.random() * 1000 }]);
        setName('');
        console.log(birthdays)
    }

    return (
    <form>
      <input onChange={nameHandler} value={name} type="text" className="todo-input" />
      <input onChange={dobHandler} type="date" />

      <button onClick={submitButtonHandler} className="todo-button" type="submit">
        <i className="fas fa-plus-square"></i>
      </button>

    </form>
    );
};

export default Form;
