import React from "react";


const Form = ({ name, setName, dob, setDob, birthdays, setBirthdays, age, setAge }) => {

    const nameHandler = (e) => {
        setName(e.target.value)
    }

    const dobHandler = (e) => {
        setDob(e.target.value)
    }

    const submitButtonHandler = (e) => {
        e.preventDefault();
        var userDateinput = dob  
        // convert user input value into date object
        var birthDate = new Date(userDateinput);
        // get difference from current date;
        var difference=Date.now() - birthDate.getTime();     
        var  ageDate = new Date(difference); 
        var calculatedAge=   Math.abs(ageDate.getUTCFullYear() - 1970);
        setAge(calculatedAge);
        setBirthdays([...birthdays, { 'name': name, 'dob': dob, 'age': age, id: Math.random() * 1000 }]);
        setName('');
    }

    return (
    <form>
      <input onChange={nameHandler} value={name} type="text" className="todo-input" />
      <input onChange={dobHandler} type="date" />

      <button onClick={submitButtonHandler} className="todo-button" type="submit">
        <i className="fas fa-plus-square"></i>
      </button>

    </form>
    );
};

export default Form;
