  }, [birthdays,status]);



  //Save to Local
  const saveLocalBirthdays = () => {
      localStorage.setItem('birthdays',JSON.stringify(birthdays));
  };

  const getLocalBirthdays = () => {
    if(localStorage.getItem('birthdays') === null) {
      localStorage.setItem('birthdays', JSON.stringify([]));
    } else {
      let birthdayLocal = JSON.parse(localStorage.getItem('birthdays'));
      setBirthdays(birthdayLocal);
    }
  }


  return (
    <div className="App">
      <header>
        <h1>Birthday Remainder</h1>
      </header>
      <Form birthdays={birthdays} 
            setBirthdays={setBirthdays} 
            inputText={inputText} 
            setInputText={setInputText}         
            />
      <BirthdayList birthdays={birthdays} setBirthdays={setBirthdays} filteredBirthdays={filteredBirthdays} />
    </div>
  );
}

export default App;
